ValidationRule.errorConditionFormula:
OR(
    AND(
        OR(
            NOT(ISBLANK(State_Province_Name__c)),
            NOT(ISBLANK(State_Province_Amount__c))
        ),
        NOT(
            OR(
                ISPICKVAL(Type__c, "State_Return"),
                ISPICKVAL(Type__c, "Provincial_Return")
            )
        )
    ),
    AND(
        OR(
            ISPICKVAL(Type__c, "Provincial_Return"),
            ISPICKVAL(Type__c, "State_Return")
        ),
        OR(
            ISBLANK(State_Province_Amount__c ),
            ISBLANK(State_Province_Name__c )
        )
    )
)
